interface TechBadgeProps {
  name: string;
}

const TechBadge: React.FC<TechBadgeProps> = ({ name }) => {
  const config = TECH_CONFIG[name] || {
    color: 'bg-gray-400/20 text-gray-200',
    icon: null
  };

  return (
    <span className={`inline-flex items-center px-3 py-1.5 rounded-full text-sm font-medium transition-colors duration-200 ${config.color}`}>
      {config.icon}
      {name}
    </span>
  );
};

export default TechBadge; 